-- Table structure for table `content_category`
-- 

CREATE TABLE `content_category` (
  `category_id` int(11) NOT NULL auto_increment,
  `category_subid` int(11) NOT NULL default '0',
  `category_image` varchar(255) default NULL,
  `category_title_en` varchar(255) default NULL,
  `category_title_th` varchar(255) NOT NULL default 'Category Title',
  `category_sdesc_en` text,
  `category_sdesc_th` text,
  `category_desc_en` text,
  `category_desc_th` text,
  `category_status` enum('approved','pending','discard') NOT NULL default 'approved',
  `category_createdtime` datetime NOT NULL default '0000-00-00 00:00:00',
  `category_createdip` varchar(15) NOT NULL default '000.000.000.000',
  `category_updatedtime` datetime NOT NULL default '0000-00-00 00:00:00',
  `category_updatedip` varchar(15) NOT NULL default '000.000.000.000',
  PRIMARY KEY  (`category_id`),
  KEY `subcategory` (`category_subid`)
) ENGINE=MyISAM  DEFAULT CHARSET=utf8 AUTO_INCREMENT=5 ;

