CREATE TABLE `product` (
  `product_id` int(11) NOT NULL auto_increment,
  `category_id` int(11) NOT NULL default '0',
  `product_type` enum('item','package') NOT NULL default 'item',
  `product_title_th` varchar(255) NOT NULL default 'Product Title',
  `product_title_en` varchar(255) default NULL,
  `product_thumbnail` varchar(255) default NULL,
  `product_image` varchar(255) default NULL,
  `product_sdesc_th` text,
  `product_sdesc_en` text,
  `product_desc_th` text,
  `product_desc_en` text,
  `product_price` int(11) NOT NULL default '0',
  `product_sprice` int(11) NOT NULL default '0',
  `product_promotion` enum('yes','no') NOT NULL default 'no',
  `product_hilight` int(1) default NULL,
  `product_skip_fee` enum('yes','no') default 'no',
  `product_status` enum('approved','pending','discard') NOT NULL default 'approved',
  `product_fea` enum('hot','new','none') NOT NULL default 'none',
  `product_seq` int(11) NOT NULL,
  `product_createdtime` datetime NOT NULL default '0000-00-00 00:00:00',
  `product_createdip` varchar(15) NOT NULL default '000.000.000.000',
  `product_updatedtime` datetime NOT NULL default '0000-00-00 00:00:00',
  `product_updatedip` varchar(15) NOT NULL default '000.000.000.000',
  PRIMARY KEY  (`product_id`),
  KEY `category` (`category_id`)
) ENGINE=MyISAM  DEFAULT CHARSET=utf8 AUTO_INCREMENT=31 ;

